content.content:
entero edad = 20

si (edad >= 18) {
    consola.mostrar("Es mayor de edad")
} sino {
    consola.mostrar("Es menor de edad")
}

para (entero var i = 0; i < 3; i++) {
    consola.mostrar(t"Iteración {i}")
}

texto saludar(texto nombre) {
    retornar "Hola " + nombre
}

consola.mostrar(saludar("María"))

objeto Persona {
    publico:
        texto nombre
        entero edad

        Persona(texto n, entero e) {
            ambiente.nombre = n
            ambiente.edad = e
        }

        vacio saludar() {
            imprimir(t"Hola, soy {ambiente.nombre} y tengo {ambiente.edad} años.")
            imprimir(t"Mi identificador privado es {ambiente.id}, no le digas a nadie, shhh!!!")
        }

    privado:
        texto id = "PER-001"
}

// Uso del objeto
Persona juan = nuevo Persona("Juan López", 30)
juan.saludar()

importar { Matemática } desde "quetzal/matemática"

objeto Punto {
    privado:
        entero var ejeX
        entero var ejeY
    público:
        Punto(entero ejeX, entero ejeY){
            ambiente.ejeX = ejeX
            ambiente.ejeY = ejeY
        }

        entero obtenerX(){
            retornar ambiente.ejeX
        }

        entero obtenerY(){
            retornar ambiente.ejeY
        }
}

lista var puntos = []
puntos.agregar(nuevo Punto(3, 2))
puntos.agregar(nuevo Punto(23, 43))
puntos.agregar(nuevo Punto(1, 3))

entero total_x = 0
entero total_y = 0

para (entero i = 0; i < puntos.longitud(); i++) {
    total_x += puntos[i].obtenerX()
    total_y += puntos[i].obtenerY()
}

número punto_medio_x = total_x / puntos.longitud()
número punto_medio_y = total_y / puntos.longitud()

consola.mostrar(t"El punto intermedio entre los objetos es: ({Matemática.redondear(punto_medio_x, 2)}, {Matemática.redondear(punto_medio_y, 2)})")
